import React, { Component } from "react";
import styled from "styled-components";

export const Form = styled.form`
  width: 300px;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 0 auto;
  width: 70vw;
`;

export const Input = styled.input`
  width: 300px;
  height: 35px;
  border: 1px solid #ccc;
  background-color: #fff;
`;

export const Button = styled.button`
  width: 300px;
  height: 35px;
  background-color: #5995ef;
  color: #fff;
  border-radius: 3px;
`;

// Text

export const Title = styled.h1`
  font-family: 'Raleway', sans-serif;
  font-weight: 600;
  color: #4d4d4d;
  font-size: 2.2em;
`;



export default class EmailForm extends Component {
  render() {
    return (<Form>
      <Title>Sesizari(timp de raspuns maxim 48h)</Title>
      <Input type="text" name="email" placeholder="Nume" />
      <Input type="text" name="email" placeholder="Prenume" />
      <Input type="text" name="email" placeholder="Numar Telefon" />
      <Input type="text" name="email" placeholder="Email" />
      <Input type="text" name="email" placeholder="adresa" />
      <Input type="text" name="email" placeholder="Va rugam detaliati sesizarea dumneavoastra" />
      <Button type="submit">Trimite</Button>
    </Form>
    );
  }
}
