import React, { Component } from 'react';


class HideAndShow extends Component {
    state = { 
        items: [{
            imgURL: "https://picsum.photos/200",
            title: "capac otel",
            pret: "85 lei"
        }],
        showContent: false

     }



     toggleContentHandler = () => {
         const doesShow = this.state.showContent;
         this.setState({showContent: !doesShow});
     }

    render() { 

        let content = null;

        if (this.state.showContent) {
            content = (
                    <div>
                        <h1>{this.state.items[0].title}</h1>
                        <p>{this.state.items[0].pret}</p>
                    </div>

            );
        }

        return ( 
            <div>
            <imgn url={this.state.items[0].imgURL} onClick={this.toggleContentHandler} />
            {content}
            </div>
         );
    }
}

export default HideAndShow;
