import React, { Component } from "react";
import styled from "styled-components";
import ReactPlayer from "react-player";


const Container = styled.div`
  padding: 30px;
`;

const ImgContainer = styled.div`
  padding: 30px;
  position: relative;
`;

const Row = styled.div`
  padding: 1px;
  &::after {
    content: "";
    display: table;
    clear: both;
  }
`;

const Column66 = styled.div`
  float: left;
  width: 66.66666%;
  padding: 20px;
  @media screen and (max-width: 1000px) {
    width: 100%;
    text-align: center;
  img {
    margin: auto;
  }
}
`;

const Column33 = styled.div`
  float: left;
  width: 33.33333%;
  padding: 20px;
  @media screen and (max-width: 1000px) {
    width: 100%;
    text-align: center;
  img {
    margin: auto;
  }
}

`;

const BigTitle = styled.h1`
  font-size: 3rem;
  color: #1091fc;
  padding: 40px;
`;

const Title = styled.h1`
  font-size: 3rem;
  color: #1091fc;
  padding: 40px;

`;

const Speciffications = styled.ul`
list-style-type: none;

  width: 85vw;
  display: flex;
  flex-direction: column;
  color: black;
  text-align: center;
  padding: 0 30px;
  padding-bottom: 40px;
  align-items: center;
  width: 100%;
  margin: 0 auto;
  line-height: 1.5;
  @media (max-width: 768px) {
    padding: 10px;
    width: 100%;
  }
`;

const Item = styled.li`
  width: 100%;
  font-size: 1.5rem;
`
const Paragraph = styled.p`
  padding: 10px;
  font-size: 1.5rem;
  text-align: left;
`;

const Span  = styled.p`
  font-size: 3rem;
  color: #1091fc;
  text-align: center;
`;
export default class Content extends Component {
  render() {
    return (
      <div>
        <BigTitle>
          Protejează ce e al tău cu nouă barieră de parcare electrică cu
          acționare prin telecomandă!
        </BigTitle>

        <Container>
          <Row>
            <Column66>
              <ReactPlayer
                url="https://www.edics.ro/wp/wp-content/uploads/2018/06/Merged_edics_20180_edics-1.mp4?_=1"
                autostart="0"
                controls
                width="335"
                height="471"
              />
            </Column66>
            <Column33>
              <Paragraph>
                Față de T-urile convenționale nu mai trebuie să te dai jos de
                fiecare dată din mașină pentru a descuia lacătul, să cobori sau
                să ridici dispozitivul. <Span>Toate aceste lucruri costă timp, timpul
                tău prețios!</Span>
              </Paragraph>
            </Column33>
          </Row>
        </Container>

        <Container>
          <Row>
            <Column66>
              <Title>Specificații și funcții</Title>
              <Speciffications >
                <Item>Distanță de acționare de până la 20 metri</Item>
                <Item>Temperaturi de funcționare de la -40 la +60 grade</Item>
                <Item>Dispozitivul este asigurat contra ploilor sau zăpezilor, functionand chiar și submersat în apă</Item>
                <Item>Funcționează cu o baterie de 6V ce îți oferă până la 6 luni de utilizări zilnice cu o singură încărcare</Item>
              </Speciffications>
            </Column66>
            <Column33>
              <ImgContainer>
              <img
                src="https://picsum.photos/300/300"
                alt="App"
                width="300px"
                height="auto"
              />
              </ImgContainer>
            </Column33>
          </Row>
        </Container>

         <Container>
          <Row>
          <Column33>
              <ImgContainer>
              <img
                src="https://picsum.photos/300/300"
                alt="App"
                width="300px"
                height="auto"
              />
              </ImgContainer>
            </Column33>
            <Column66>
            <Speciffications >
                <Item>Bateria se încarcă repede și ușor la orice priză prin intermediul incarcatorului inclus în pachet</Item>
                <Item>Avertizare sonoră anti-forțare cât și la ridicare-coborâre dispozitiv</Item>
                <Item>Acesta suportă șocuri/greutate de până la 2 tone</Item>
                <Item>Asigurare anti-furt prin intermediul încuietorii ce nu permite deschiderea dispozitivului</Item>
                <Item>Dispozitivul se fixează în asfalt în 3 puncte, acoperite de capacul încuiat</Item>
                <Item>Înălțime minimă: 6 cm Înălțime maximă 40 cm</Item>
              </Speciffications>
            </Column66>

          </Row>
        </Container>


        <Container>
          <Row>
            <Column66>
              <Title>Pachetul conține</Title>
              <Paragraph>
                Dispozitiv parcare electric 2 telecomenzi 2 chei pentru
                încuierea capacului exterior 3 șuruburi fixare asfalt 1
                încărcător baterie Instrucțiuni folosire
              </Paragraph>
            </Column66>
            <Column33>
            <ImgContainer>
              <img
                src="https://picsum.photos/300/300"
                alt="App"
                width="100%" height="auto"
              />
              </ImgContainer>
            </Column33>
          </Row>

        </Container>
      </div>
    );
  }
}
