import React, { Component } from "react";
import styled from "styled-components";
import ReactPlayer from "react-player";
// const Container = styled.div`

const backgroundColor = "#4F5659";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  line-height: 2em;
  background-color: ${backgroundColor};
`;

const ParagraphContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  padding: 50px;
  background-color: ${backgroundColor};
  justify-items: center;
  margin: 0 auto;
  width: 85vw;
  @media (max-width: 768px) {
    padding: 0;
    width: 100%;
  }
  @media (min-width: 768px) {
    margin: 5px;
    padding: 5px;
    margin: 0 auto;

  }
`;

const Paragraph = styled.ul`
  width: 85vw;
  display: flex;
  flex-direction: column;
  font-size: 1.4rem;
  color: white;
  background-color: ${backgroundColor};
  padding: 0 30px;
  padding-bottom: 40px;
  align-items: center;
  text-align: left;
  width: 100%;
  margin: 0 auto;
  line-height: 1.5;
  @media (max-width: 768px) {
    padding: 10px;
    width: 100%;
  }
`;

const Propozitie = styled.li`
  width: 100%;
`;

const Title = styled.h1`
  font-size: 3rem;
  padding: 50px 50px 50px 50px;
  background-color: ${backgroundColor};
  color: #fff238;
  @media (max-width: 768px) {
    font-size: 2.5rem;
    padding: 10px;
    width: 100%;
  }
`;

const Cutie = styled.div`
  display: flex;
  flex-direction: column;
  flex: 1fr;
  color: white;
`;

const Title2 = styled.h2`
  font-size: 2.5rem;
  background-color: ${backgroundColor};
  color: white;
  padding: 10px;
  @media (max-width: 768px) {
    padding: 0 10px;
    width: 100%;
    font-size: 2.25rem;
  }
`;

const VideoContainer = styled.div`
  margin: 0px;
  padding: 10px;
  display: relative;
  width: 100%;
  height: 100%;
  z-index: 5;
  @media (max-width: 768px) {
    padding: 10px;
    width: 100%;
    height: 100%;
  }
`;

const HR = styled.div`
  border: 0;
  height: 3px;
`;

const BR = styled.br`
  margin-top: 10px;
`;

export default class Content extends Component {
  render() {
    return (
      <div>
        <Container>
          <VideoContainer>
            <ReactPlayer
              url="https://www.edics.ro/wp/wp-content/uploads/2018/06/Merged_edics_20180_edics-1.mp4?_=1"
              autostart="0"
              controls
              width="100%"
              height="100%"
            />
          </VideoContainer>

          <Title>Specificații și funcții</Title>
          <ParagraphContainer>
            <Paragraph>
              <Propozitie>
                Distanță de acționare de până la 20 metri.
              </Propozitie>
              <Propozitie>
                Temperaturi de funcționare de la -40 la +60 grade.
              </Propozitie>
              <Propozitie>
                Dispozitivul este asigurat contra ploilor sau zăpezilor,
                functionand chiar și submersat în apă.
              </Propozitie>
              <Propozitie>
                Funcționează cu o baterie de 6V ce îți oferă până la 6 luni de
                utilizări zilnice cu o singură încărcare.
              </Propozitie>
              <Propozitie>
                Bateria se încarcă repede și ușor la orice priză prin
                intermediul incarcatorului inclus în pachet.
              </Propozitie>
            </Paragraph>
            <Paragraph>
              <Propozitie>
                Avertizare sonoră anti-forțare cât și la ridicare-coborâre
                dispozitiv.
              </Propozitie>
              <Propozitie>
                Acesta suportă șocuri/greutate de până la 2 tone.
              </Propozitie>
              <Propozitie>
                Asigurare anti-furt prin intermediul încuietorii ce nu permite
                deschiderea dispozitivului.
              </Propozitie>
              <Propozitie>
                Dispozitivul se fixează în asfalt în 3 puncte, acoperite de
                capacul încuiat.
              </Propozitie>
              <Propozitie>Înălțime minimă: 6 cm</Propozitie>
              <Propozitie>Înălțime maximă 40 cm</Propozitie>
            </Paragraph>
          </ParagraphContainer>
          <Cutie>

            Pachetul conține:
            <Propozitie>Dispozitiv parcare electric</Propozitie>
            <Propozitie>2 telecomenzi</Propozitie>
            <Propozitie>2 chei pentru încuierea capacului exterior</Propozitie>
            <Propozitie>3 șuruburi fixare asfalt</Propozitie>
            <Propozitie>1 încărcător baterie</Propozitie>
            <Propozitie>Instrucțiuni folosire</Propozitie> Pentru comenzi
            folosiți numărul din datele de contact.
          </Cutie>
        </Container>
      </div>
    );
  }
}
