import React, { Component } from "react";
import styled from "styled-components";



const Container = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  padding: 50px;
  background-color: #3696ff;
  margin: 0 auto;
  width: 85vw;
`

const Item = styled.div`
  width: 100%;
  height: auto;
`


export default class ItemsTable extends Component {

state = {
  items: [{
      url:"https://picsum.photos/200",
      title:"Capac Otel",
      price:85
  },

  {
      url:"https://picsum.photos/200",
      title:"Motor",
      price:180
  },

  {
      url:"https://picsum.photos/200",
      title:"Brat Metalic",
      price:80
  },

  {
  url:"https://picsum.photos/200",
  title:"Telecomanda",
  price:50
  },

  {
  url:"https://picsum.photos/200",
  title:"Acumulator reincarbail",
  price: 70

  }]
}  


  render() {
    return (
      <div>
          <Container>
              {this.state.items.map(item => {
                  <Item 
                  imgURL={item.name}
                  title={item.title}
                  price={item.price}
                />
              })}
          </Container>
      </div>
    );
  }
}
