import React from "react";
import styled from "styled-components";


const Container = styled.div`
  display: flex;
  flex-direction: column;
  line-height: 2em;
  background-color: #3696ff;
`;

const Title = styled.h1`
 text-align: center;
  width: 85vw;
  display: flex;
  font-size: 1.7rem;
  color: #3696ff;
  background-color: white;
  padding: 0 30px;
  text-align: left;
  width: 100%;
  margin: 0 auto;
  @media (max-width: 768px) {
    padding: 10px;
    width: 100%;
  }
`;

const Price = styled.p`
  font-size: 1.7rem;
  text-align: right;
  padding-top: 30px;
`;

const item = props => {
    return (
      <div className="item">

      <img  src={props.url}/>
        <Title>
        {props.name}
        </Title>



        <Price>
        {props.price}
        </Price>
      </div>
    );
  };

  export default item;
