import React, { Component } from "react";
import styled from "styled-components";
import ReactPlayer from "react-player";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  line-height: 2em;
  background-color: #3696ff;
`;

const ParagraphContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  padding: 50px;
  background-color: #3696ff;
  margin: 0 auto;
  width: 85vw;
  @media (max-width: 768px) {
    font-size: 1rem;
    width: 90vw;
    margin:5px;
    padding: 5px;
    margin: 0 auto;
  }
  @media (min-width: 768px) {
    font-size: 1rem;
    width: 90vw;
    margin:5px;
    padding: 5px;
    margin: 0 auto;
    grid-template-columns: repeat(auto-fit, minmax(600px, 1fr));
  }
`;

const Paragraph = styled.p`
  display: flex;
  font-size: 1.7rem;
  color: white;
  background-color: #3696ff;
  padding: 0 30px;
  text-align: left;
  padding: 10px;
  width: 100%;
  @media (max-width: 768px) {
    font-size: 1rem;
    width: 100%;
    margin:0;
    padding: 0;
  }
  @media (min-width: 768px) {
    font-size: 1.5rem;
    width: 100%;
    margin:0;
    padding: 0;
  }

`;

const Title = styled.h1`
  font-size: 3rem;
  padding: 50px 50px 0 50px;
  background-color: #3696ff;
  color: #fff238;
  @media (max-width: 768px) {
    font-size: 1.75rem;
    width: 100%;
    margin:0;
    padding: 0;
  }
  @media (min-width: 768px) {
    font-size: 2.25rem;
    width: 100%;
    margin:10px;
    padding: 0;
  }

`;

const Title2 = styled.h2`
  font-size: 2.5rem;
  background-color: #3696ff;
  color: white;
  @media (max-width: 768px) {
    font-size: 1.25rem;
  }
  @media (min-width: 768px) {
    font-size: 1.75rem;
  }
`;

const VideoContainer = styled.div`
  margin: 0px;
  display: relative;
  width: 100%;
  height: 600px;
  @media (max-width: 768px) {
    width: 100%;
    margin:0;
    padding: 0;
    height: 300px;
  }
`;

const HR = styled.div`
  border: 0;
  height: 3px;
`;

export default class Content extends Component {
  render() {
    return (
      <div>
        <Container>
          <VideoContainer>
            <ReactPlayer
              url="https://www.edics.ro/wp/wp-content/uploads/2018/06/Merged_edics_20180_edics-1.mp4?_=1"
              playing
              controls
              width="100%"
              height="100%"
            />
          </VideoContainer>
          <Title>Ai ajuns acasă și îți găsești locul de parcare ocupat?</Title>
          <Title2>Edics îți oferă soluția.</Title2>

          <ParagraphContainer>
          <Paragraph>
              Protejează ce e al tău cu nouă barieră de parcare electrică cu
            acționare prin telecomandă.
          </Paragraph>
          <Paragraph>Nu mai trebuie să te dai jos de
            fiecare dată din mașină.
          </Paragraph>
          <Paragraph>
          Toate aceste lucruri costă timp, timpul tău prețios.
          </Paragraph>
          </ParagraphContainer>

          <Title>Specificații și funcții</Title>
          <ParagraphContainer>
          <Paragraph>
            Distanță de acționare de până la 20 metri. Temperaturi de
            funcționare de la -40 la +60 grade.
            Dispozitivul este asigurat contra ploilor sau zăpezilor, functionand
            chiar și submersat în apă.
            Funcționează cu o baterie de 6V ce îți oferă până la 6 luni de
            utilizări zilnice cu o singură încărcare. Bateria se încarcă repede
            și ușor la orice priză prin intermediul incarcatorului inclus în
            pachet.
            Avertizare sonoră anti-forțare cât și la ridicare-coborâre
            dispozitiv.
          </Paragraph>
          <Paragraph>
            Acesta suportă șocuri/greutate de până la 2 tone.
            Asigurare anti-furt prin intermediul încuietorii ce nu permite
            deschiderea dispozitivului.
            Dispozitivul se fixează în asfalt în 3 puncte, acoperite de capacul
            încuiat.
            Înălțime minimă: 6 cm Înălțime maximă 40 cm Pachetul conține:
            Dispozitiv parcare electric 2 telecomenzi 2 chei pentru încuierea
            capacului exterior 3 șuruburi fixare asfalt 1 încărcător baterie
            Instrucțiuni folosire Pentru comenzi folosiți numărul din datele de
            contact.
          </Paragraph>
          </ParagraphContainer>
        </Container>
      </div>
    );
  }
}
