import React, { Component } from "react";
import styled from "styled-components";
import ReactPlayer from "react-player";

const Section = styled.section`
  margin: 0;
  overflow: auto;
  padding: 2em 0;
  @media (min-width: 1025px) {
    padding: 4em 0;
  }
  @media (min-width: 769px) {
    padding: 3.5em 0;
  }
  @media (min-width: 600px) {
    padding: 3em 0;
  }
`;

const Container = styled.div`
  margin: 0 auto;
  max-width: 76em;
  padding-left: 2em;
  padding-right: 2em;
  @media (max-width: 600px) {
    padding-left: 1.5em;
    padding-right: 1.5em;
  }
`;

const ContainerBox = styled.div`
  display: flex;
  align-items: center;
  @media (max-width: 600px) {
    align-items: center;
    flex-direction: column;
    &.reverse {
      flex-direction: column-reverse;
    }
  }
`;

const ContainerBoxLeft = styled.div`
  margin-right: 1em;
  padding: 0 5em;
  @media (max-width: 769px) {
    padding: 0 2em;
  }
  @media (max-width: 600px) {
    margin-bottom: 4em;
    margin-left: 0;
    margin-right: 0;
  }
  &.video {
    padding-top: 80px;
    @media (max-width: 600px) {
      margin-bottom: 0px;
      padding: 0px;
    }
  }

  &.reverseTwo {
    margin-right: 0;
    margin-left: 1em;
    @media (max-width: 769px) {
      padding: 0 2em;
    }
    @media (max-width: 600px) {
      margin-bottom: 4em;
      margin-left: 0;
      margin-right: 0;
    }
  }
`;

const ContainerBoxLeftContent = styled.div`
  height: 22em;
  width: 22em;
  @media (max-width: 769px) {
    height: 20em;
    width: 20em;
  }
`;

const ContainerBoxRight = styled.div`
  flex: 1;
  text-align: left;
  &.firstTitleRight {
    margin-top: -75px;
  }
`;

const LineContainer = styled.div`
  clear: both;
  margin: 0;
  overflow: auto;
  padding: 2em 0;
  @media (min-width: 1025px) {
    padding: 4em 0;
  }
  @media (min-width: 769px) {
    padding: 3.5em 0;
  }
  @media (min-width: 600px) {
    padding: 3em 0;
  }
`;

const Line = styled.div`
  border-bottom: none;
  border-left-width: 0;
  border-right-width: 0;
  border-top: 1px solid #ddd;
  margin: 0;
`;

const Title = styled.h2`
  font-size: 2.25em;
  letter-spacing: 0.01em;
  line-height: 1.4em;
  margin-bottom: 0.5em;
  @media (max-width: 600px) {
    text-align: center;
  }
  &.firstTitle {
    margin-top: 1em;
    font-size: 1.85em;
  }
`;

const Text = styled.p`
  font-size: 1.25em;
  line-height: 1.65em;
  margin-bottom: 0;
  color: #777777;
`;

const Paragraph = styled.ul``;

export default class Content extends Component {
  render() {
    return (
      <div>
        <Section>
          <Container>
            <Title className="firstTitle">
              Protejează ce e al tău cu nouă barieră de parcare electrică cu
              acționare prin telecomandă!
            </Title>
          </Container>
        </Section>

        <Section>
          <Container>
            <ContainerBox>
              <ContainerBoxLeft className="video">
                <ContainerBoxLeftContent>
                  <ReactPlayer
                    url="https://www.edics.ro/wp/wp-content/uploads/2018/06/Merged_edics_20180_edics-1.mp4?_=1"
                    autostart="0"
                    controls
                    width="335"
                    height="471"
                  />
                </ContainerBoxLeftContent>
              </ContainerBoxLeft>
              <ContainerBoxRight className="firstTitleRight">
                <Title>
                  Toate aceste lucruri costă timp, timpul tău prețios!
                </Title>
                <Text>
                  Față de T-urile convenționale nu mai trebuie să te dai jos de
                  fiecare dată din mașină pentru a descuia lacătul, să cobori
                  sau să ridici dispozitivul.
                </Text>
              </ContainerBoxRight>
            </ContainerBox>{" "}
          </Container>
        </Section>
        <LineContainer>
          <Container>
            <Line />
          </Container>
        </LineContainer>
        <Section>
          <Container>
            <ContainerBox className="reverse">
              <ContainerBoxRight>
                <Title>Specificații și funcții</Title>
                <Text>
                  Distanță de acționare de până la 20 metri. Temperaturi de
                  funcționare de la -40 la +60 grade. Dispozitivul este asigurat
                  contra ploilor sau zăpezilor, functionand chiar și submersat
                  în apă. Funcționează cu o baterie de 6V ce îți oferă până la 6
                  luni de utilizări zilnice cu o singură încărcare. Bateria se
                  încarcă repede și ușor la orice priză prin intermediul
                  incarcatorului inclus în pachet. Avertizare sonoră
                  anti-forțare cât și la ridicare-coborâre dispozitiv. Acesta
                  suportă șocuri/greutate de până la 2 tone. Asigurare anti-furt
                  prin intermediul încuietorii ce nu permite deschiderea
                  dispozitivului. Dispozitivul se fixează în asfalt în 3 puncte,
                  acoperite de capacul încuiat. Înălțime minimă: 6 cm Înălțime
                  maximă 40 cm
                </Text>
              </ContainerBoxRight>
              <ContainerBoxLeft className="reverseTwo">
                <ContainerBoxLeftContent>
                  <img
                    src="https://picsum.photos/300/300"
                    alt="App"
                    width="300"
                    height="300"
                  />
                </ContainerBoxLeftContent>
              </ContainerBoxLeft>
            </ContainerBox>
          </Container>
        </Section>
        <LineContainer>
          <Container>
            <Line />
          </Container>
        </LineContainer>
        <Section>
          <Container>
            <ContainerBox>
              <ContainerBoxLeft>
                <ContainerBoxLeftContent>
                  <img
                    src="https://picsum.photos/300/300"
                    alt="App"
                    width="300"
                    height="300"
                  />
                </ContainerBoxLeftContent>
              </ContainerBoxLeft>
              <ContainerBoxRight>
                <Title>Pachetul conține</Title>
                <Text>
                  Dispozitiv parcare electric 2 telecomenzi 2 chei pentru
                  încuierea capacului exterior 3 șuruburi fixare asfalt 1
                  încărcător baterie Instrucțiuni folosire
                </Text>
              </ContainerBoxRight>
            </ContainerBox>
          </Container>
        </Section>
        <LineContainer>
          <Container>
            <Line />
          </Container>
        </LineContainer>
      </div>
    );
  }
}
