import React, { Component } from "react";
import styled from "styled-components";
import ReactPlayer from "react-player";
import { keyFrameTwo } from "./Animation";

const Section = styled.section`
  margin: 0;
  overflow: auto;
  padding: 2em 0;
  @media (min-width: 1025px) {
    padding: 4em 0;
  }
  @media (min-width: 769px) {
    padding: 3.5em 0;
  }
  @media (min-width: 600px) {
    padding: 3em 0;
  }
`;

const Container = styled.div`
  margin: 0 auto;
  max-width: 76em;
  padding-left: 2em;
  padding-right: 2em;
  @media (max-width: 600px) {
    padding-left: 1.5em;
    padding-right: 1.5em;
  }
`;

const ContainerBox = styled.div`
  display: flex;
  align-items: center;
  @media (max-width: 982px) {
    flex-direction: column;
    &.reverse {
      flex-direction: column-reverse;
    }
  }
`;

const ContainerBoxLeft = styled.div`
  margin-right: 1em;
  padding: 0 2em;
  @media (max-width: 769px) {
    padding: 0 2em;
  }
  @media (max-width: 982px) {
    margin-bottom: 4em;
    margin-left: 0;
    margin-right: 0;
  }
  &.video {
    /* padding-top: 80px; */
    @media (max-width: 982px) {
      margin-bottom: 4em;
      padding: 0px;
    }
  }

  &.reverseTwo {
    @media (max-width: 769px) {
      padding: 0 2em;
    }
    @media (max-width: 600px) {
      margin-bottom: 4em;
    }
  }
`;

const ContainerBoxLeftContent = styled.div`
  &.image {
    height: 22em;
    width: 22em;
    @media (max-width: 769px) {
      height: 20em;
      width: 20em;
    }
  }
`;

const ContainerBoxRight = styled.div`
  flex: 1;
  text-align: left;
  /* min-width: 350px; */
  &.firstTitleRight {
    @media (max-width: 982px) {
      /* margin-top: -75px; */
    }
  }
`;

const LineContainer = styled.div`
  margin: 0;
  overflow: auto;
  padding: 2em 0;
  @media (max-width: 600px) {
    padding-left: 1.5em;
    padding-right: 1.5em;
  }
`;

const Line = styled.div`
  border-bottom: none;
  border-left-width: 0;
  border-right-width: 0;
  border-top: 1px solid #dddddd;
  margin: 0;
`;

const Title = styled.h2`
  font-size: 2.25em;
  letter-spacing: 0.01em;
  line-height: 1.4em;
  margin-bottom: 0.5em;
  @media (max-width: 982px) {
    text-align: center;
  }
  &.firstTitle {
    margin-top: 1em;
    font-size: 1.85em;
    @media (max-width: 845px) {
      max-width: 80%;
      margin: 0 auto;
    }
  }
`;

const underlineText = styled.span`
  text-decoration: underline;
  font-size: inherit;
  letter-spacing: inherit;
`;

const Text = styled.p`
  font-size: 1.25em;
  line-height: 1.65em;
  margin-bottom: 0;
  color: #777777;
  @media (max-width: 982px) {
    text-align: center;
  }
`;

const Paragraph = styled.ul`
  display: flex;
  flex-direction: column;
  @media (max-width: 982px) {
    align-items: center;
  }
`;

const Propozitie = styled.li`
  text-decoration: none;
  list-style: none;
  display: inline-flex;
`;

const SvgIcon = styled.div`
  padding-top: 3px;
  padding: 3px;
  fill: #3c8fd3;
`;

export default class Content extends Component {
  render() {
    return (
      <div>
        <Section>
          <Container>
            <Title className="firstTitle">
              Protejează ce e al tău cu noua {/* <underlineText> */}
              barieră de parcare electrică
              {/* </underlineText> */}
              <br />
              cu acționare prin telecomandă!
            </Title>
          </Container>
        </Section>

        <Section>
          <Container>
            <ContainerBox>
              <ContainerBoxLeft className="video">
                <ContainerBoxLeftContent>
                  <ReactPlayer
                    url="https://www.edics.ro/wp/wp-content/uploads/2018/06/Merged_edics_20180_edics-1.mp4?_=1"
                    autostart="0"
                    controls
                    width="335"
                    height="471"
                  />
                </ContainerBoxLeftContent>
              </ContainerBoxLeft>
              <ContainerBoxRight className="firstTitleRight">
                <Title>Mai mult timp liber!</Title>
                <Text>
                  {/* Față de T-urile convenționale nu mai trebuie să te dai jos de
                  fiecare dată din mașină pentru a descuia lacătul, să cobori
                  sau să ridici dispozitivul. */}
                  Să te dai jos de fiecare dată din masină pentru a descuia
                  lacătul și a ridica T-ul este de domeniul TRECUTULUI.
                </Text>
              </ContainerBoxRight>
            </ContainerBox>{" "}
          </Container>
        </Section>
        <LineContainer>
          <Container>
            <Line />
          </Container>
        </LineContainer>
        <Section>
          <Container>
            <ContainerBox className="reverse">
              <ContainerBoxRight>
                <Title>Specificații și funcții</Title>
                <Text>
                  <Paragraph>
                    <Propozitie>Distanță de acționare de 20 metri.</Propozitie>
                    <Propozitie>
                      Temperaturi funcționare -40 / +60 grade.
                    </Propozitie>
                    <Propozitie>
                      Rezistent contra ploilor si zapezilor.
                    </Propozitie>
                    <Propozitie>
                      Rezista 6 luni cu o singura incarcare.
                    </Propozitie>
                    <Propozitie>Asigurare anti-furt.</Propozitie>
                  </Paragraph>
                </Text>
              </ContainerBoxRight>
              <ContainerBoxLeft className="reverseTwo">
                <ContainerBoxLeftContent>
                  <img
                    className="image"
                    src="https://picsum.photos/300/300"
                    alt="App"
                    width="300"
                    height="300"
                  />
                </ContainerBoxLeftContent>
              </ContainerBoxLeft>
            </ContainerBox>
          </Container>
        </Section>
        <LineContainer>
          <Container>
            <Line />
          </Container>
        </LineContainer>
        <Section>
          <Container>
            <ContainerBox>
              <ContainerBoxLeft>
                <ContainerBoxLeftContent>
                  <img
                    className="image"
                    src="https://picsum.photos/300/300"
                    alt="App"
                    width="300"
                    height="300"
                  />
                </ContainerBoxLeftContent>
              </ContainerBoxLeft>
              <ContainerBoxRight>
                <Title>Pachetul conține:</Title>
                <Text>
                  <Paragraph>
                    <Propozitie> - Dispozitiv parcare electric</Propozitie>
                    <Propozitie>- 2 telecomenzi</Propozitie>
                    <Propozitie>
                      - 2 chei pentru încuierea capacului exterior
                    </Propozitie>
                    <Propozitie> - 3 șuruburi fixare asfalt</Propozitie>
                    <Propozitie> - Incărcător baterie</Propozitie>
                    {/* Instrucțiuni folosire */}
                  </Paragraph>
                </Text>
              </ContainerBoxRight>
            </ContainerBox>
          </Container>
        </Section>
        <LineContainer>
          <Container>
            <Line />
          </Container>
        </LineContainer>
      </div>
    );
  }
}
